void being_honor_delta( simulated_being *value, n_int delta )
{
    n_int honor_value = value->delta.honor;
    if ( delta > 0 )
    {
        if ( ( honor_value + delta ) > 255 )
        {
            value->delta.honor = 255;
            return;
        }
    }
    else
    {
        if ( ( honor_value + delta ) < 0 )
        {
            value->delta.honor = 0;
            return;
        }
    }
    value->delta.honor += ( n_byte )delta;
}

