/**
 * @brief Returns the type of formatting of a three byte instruction
 * @param instruction The instruction type
 * @param command Whether the subsequent two bytes are constants or variables
 * @param value0 The first argument
 * @param value1 The second argument
 * @return The format type used to display a three byte instruction
 */
static enum BC_FORMAT brain_format( n_byte instruction, n_byte command, n_byte value0, n_byte value1 )
{
    n_byte is_constant0 = ( ( command & BRAINCODE_CONSTANT0_BIT ) != 0 );
    n_byte is_constant1 = ( ( command & BRAINCODE_CONSTANT1_BIT ) != 0 );

    switch ( instruction )
    {
    case BRAINCODE_AND:
    case BRAINCODE_OR:
    case BRAINCODE_MOV:
    case BRAINCODE_ADD:
    case BRAINCODE_SUB:
    case BRAINCODE_MUL:
    case BRAINCODE_MOD:
        if ( ( !is_constant0 ) && ( !is_constant1 ) )
        {
            return BC_FORMAT_A;
        }
        return BC_FORMAT_C;
        break;
    case BRAINCODE_JMZ:
    case BRAINCODE_JMN:
    case BRAINCODE_DJN:
        return BC_FORMAT_E;
        break;
    case BRAINCODE_SEQ:
    case BRAINCODE_SNE:
    case BRAINCODE_SLT:
        if ( ( !is_constant0 ) && ( !is_constant1 ) )
        {
            return BC_FORMAT_A;
        }
        return BC_FORMAT_F;
        break;
    case BRAINCODE_DAT0:
    case BRAINCODE_DAT1:
    case BRAINCODE_JMP:
        return BC_FORMAT_C;
        break;
    case BRAINCODE_INV:
        if ( is_constant0 )
        {
            return BC_FORMAT_G;
        }

        return BC_FORMAT_H;

        break;
    case BRAINCODE_STP:
    case BRAINCODE_LTP:
        if ( ( is_constant0 ) && ( !is_constant1 ) )
        {
            return BC_FORMAT_F;
        }
        if ( ( is_constant0 ) && ( is_constant1 ) )
        {
            return BC_FORMAT_C;
        }
        if ( ( !is_constant0 ) && ( is_constant1 ) )
        {
            return BC_FORMAT_E;
        }
        break;
    }
    return BC_FORMAT_A;
}

