static void sim_console_clean_up( void )
{
    if ( ( io_command_line_execution() == 0 ) || sim_quit_value )
    {
        return;
    }

    sim_quit_value = 1;

    command_quit( 0L, 0L, 0L );

    while ( command_executing() ) {}
}


static n_console_input *input_function = &io_console_entry_clean;
